import ReactDOM from "react-dom"
import React, { Suspense, useEffect, useRef, useMemo, useState, setState, Fragment } from "react"
import { Canvas, Dom, useLoader, useFrame } from "react-three-fiber"
import { TextureLoader, LinearFilter } from "three"
import lerp from "lerp"
import { Text, MultilineText } from "./components/Text"
import Diamonds from "./diamonds/Diamonds"
import Plane from "./components/Plane"
import { Block, useBlock } from "./blocks"
import state from "./store"
import "./styles.css"
import ModalVideo from 'react-modal-video'
import "../node_modules/modal-video/js/jquery-modal-video";
import {
  BrowserRouter as Router,
  Switch,
  Route,
} from "react-router-dom";
import ArtAssisting from "./components/ArtAssisting.js";
import NavBar from "./components/NavBar.js"

function Startup() {
  const ref = useRef()
  useFrame(() => (ref.current.material.opacity = lerp(ref.current.material.opacity, 0, 0.025)))
  return <Plane ref={ref} color="#0e0e0f" position={[0, 0, 200]} scale={[100, 100, 1]} />
}


function ProductionDesign() {
  const production_design = state.paragraphs.production_design

  const [isOpen, setOpen] = useState(false)
  const [isOpenProdDesign0, setOpenProdDesign0] = useState(false)
  const [isOpenProdDesign1, setOpenProdDesign1] = useState(false)


  function prodDesign0Action(boolean) {
    console.log("CLICKED")
    setOpenProdDesign0(boolean)
  }
  function prodDesign1Action(boolean) {
    console.log("CLICKED")
    setOpenProdDesign1(boolean)
  }

  const prodDesignActions = [prodDesign0Action, prodDesign1Action]
  const is_action = [isOpenProdDesign0, isOpenProdDesign1]

  return (
    <>
      <NavBar production_design="active_tab" />


      <div class="gallery">

        {production_design.map((index) =>
          <React.Fragment>
            <ModalVideo channel={index.channel} autoplay isOpen={is_action[index.action]} videoId={index.video} onClose={() => prodDesignActions[index.action](false)} />
            <figure class={index.css + index.action} onClick={(e) => {
              e.stopPropagation()
              prodDesignActions[index.action](true)
            }}>
              <div class="overlay">

                <img class="gallery__img" src={index.image}></img>
                <div class="text-header">{index.header}</div>
                <div class="text-footer">{index.text}</div>
              </div>

            </figure>
          </React.Fragment>
        )}




      </div>
    </>)
}

function RenderArt() {
  const render_art = state.paragraphs.render_art


  // const [isOpenProdDesign0, setOpenProdDesign0] = useState(false)


  const renderArtActions = [openModal0]

  function openModal0() {
    document.getElementById("myModal").style.display = "block";
  }

  // Close the Modal
  function closeModal0() {
    document.getElementById("myModal").style.display = "none";
  }

  var slideIndex = 1;
showSlides(slideIndex);

// Next/previous controls
function plusSlides(n) {
  showSlides(slideIndex += n);
}

// Thumbnail image controls
function currentSlide(n) {
  showSlides(slideIndex = n);
}

function showSlides(n) {
  var i;
  var slides = renderArtData.getElementsByClassName("mySlides");
  console.log(slides)
  var dots = document.getElementsByClassName("demo");
  var captionText = document.getElementById("caption");
  if (n > slides.length) {slideIndex = 1}
  if (n < 1) {slideIndex = slides.length}
  for (i = 0; i < slides.length; i++) {
    slides[i].style.display = "none";
  }
  for (i = 0; i < dots.length; i++) {
    dots[i].className = dots[i].className.replace(" active", "");
  }
  slides[slideIndex-1].style.display = "block";
  dots[slideIndex-1].className += " active";
  captionText.innerHTML = dots[slideIndex-1].alt;
}

const RenderArtData = React.forwardRef((props, ref) => {
  return    <>
   <NavBar render_art="active_tab" />


  <div class="gallery">

    {render_art.map((index) =>
      <figure class={index.css + index.action} onClick={(e) => {
        e.stopPropagation()
        renderArtActions[index.action]()
      }}>
        <div class="overlay">

          <img class="gallery__img" src={index.image}></img>
          <div class="text-header">{index.header}</div>
          <div class="text-footer">{index.text}</div>
        </div>

      </figure>
    )}




  </div>

<div id="myModal" class="modal">
<span class="close cursor" onclick="closeModal()">&times;</span>
<div class="modal-content">

<div class="mySlides">
  <div class="numbertext">1 / 4</div>
  <img class="slide-image" src="blonde.png" ></img>
</div>

<div class="mySlides">
  <div class="numbertext">2 / 4</div>
  <img class="slide-image" src="blonde2.png" ></img>
</div>

<div class="mySlides">
  <div class="numbertext">3 / 4</div>
  <img class="slide-image" src="blonde3.png" ></img>
</div>

<div class="mySlides">
  <div class="numbertext">4 / 4</div>
  <img class="slide-image" src="blonde4.png" ></img>
</div>

<a class="prev" onclick="plusSlides(-1)">&#10094;</a>
<a class="next" onclick="plusSlides(1)">&#10095;</a>

<div class="column">
  <img class="demo" src="blonde.png" onclick="currentSlide(1)" alt="Nature"></img>
</div>

<div class="column">
  <img class="demo" src="blonde2.png" onclick="currentSlide(2)" alt="Snow"></img>
</div>

<div class="column">
  <img class="demo" src="blonde3.png" onclick="currentSlide(3)" alt="Mountains"></img>
</div>

<div class="column">
  <img class="demo" src="blonde4.png" onclick="currentSlide(4)" alt="Lights"></img>
</div>
</div>
</div>
</>
})

const renderArtData = useRef(null);

  return (
    <RenderArtData ref={renderArtData}/>
    )
}

function Heading({ text }) {
  return (
    <div className="heading">
      <h1>{text}</h1>
    </div>
  )
}



function Home() {
  return (
    <>
      <NavBar />
      <Heading text="Jimmy Van Twest | Art Department" />
      <ul class="cb-slideshow">
        <li>
          <span>Image 01</span>
        </li>
        <li>
          <span>Image 02</span>
        </li>
        <li>
          <span>Image 03</span>
        </li>
        <li>
          <span>Image 04</span>
        </li>
        <li>
          <span>Image 05</span>
        </li>
      </ul>
    </>
  )
}

function App() {


  return (
    <>

      <Router >
        <Switch >

          <Route exact path="/" component={Home} />
          <Route exact path="/production_design" component={ProductionDesign} />
          <Route exact path="/art_assisting" component={ArtAssisting} />
          <Route exact path="/render_art" component={RenderArt} />

        </Switch>
      </Router>


    </>
  )
}

ReactDOM.render(<App />, document.getElementById("root"))
